      }


     render(){
         return(
             <Aux>
                <Burger ingredients={this.state.ingredients}/>
                <BuildControls ingredientAdded={this.addIngredientsHandler}/>
             </Aux>
         )
     }
 }
 export default BurgerBuilder;
