import React, { Component } from 'react';
import PropTypes from 'prop-types';

import './Burger.css';

class  BurgerIngrediant extends Component{
    render(){
let ingredient=null;

switch(this.props.type){
    case('bread-bottom'):
    let ingredient=<div className="BreadBottom"></div>
    break;
    case('bread-top'):
    ingredient=<div className="BreadTop">
        <div classsName="Seeds1"></div>
        <div className="Seeds2"></div>
    </div>
    break;
    case('Meat'):
    ingredient=<div className="Meat"></div>
    break;
    case("Cheese"):
    ingredient=<div className="Cheese"></div>
    break;
    case("Bacon"):
    ingredient=<div classNme="Bacon"></div>
    break;
    case("Salad"):{
        ingredient=<div className="Salad"></div>
        break;
    }
    default:
        ingredient=null
        break;
}
return ingredient;
}
}
BurgerIngrediant.propTypes={
    type:PropTypes.string.isRequired
}

export default BurgerIngrediant;
